import React, { useEffect, useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import {Link, useHistory} from 'react-router-dom';

function ProductDetail(props)
{

    const history = useHistory();
    const [loading, setLoading] = useState(true);
    const [product, setProduct] = useState([]);

    useEffect(()=>{
        let isMounted = true;

        const category_slug = props.match.params.category;
        const product_slug = props.match.params.product;
        axios.get(`/api/viewproductdetail/${category_slug}/${product_slug}`).then(res => {
            if(isMounted)
            {
                if(res.data.status === 200)
                {
                    setProduct(res.data.product);
                    setLoading(false);
                }
                else if(res.data.status === 404)
                {
                    history.push('/collections');
                    swal("Warning",res.data.message,"error");
                }
            }
        });
        
        return () => {
            isMounted = false
        };

    },[props.match.params.category, props.match.params.product, history]);

    if(loading)
    {
        return <h4>Loading Products Details...</h4>
    }
    else
    {
    	var avail_stock = '';
    	if(product.qty > 0)
    	{
    		avail_stock = 	
    		<div>
				<label className="btn-sm btn-success px-4 mt-2">In Stock</label>
    			<div className="row">
    				<div className="col-md-3 mt-3">
    					<div className="input-group">
    						<button type="button" onClick={handleDecrement} className="input-group-text">-</button>
    						<input text="text" className="form-control text-center"/>
    						<button type="button" onClick={handleIncrement} className="input-group-text">+</button>
						</div>
					</div>
					<div className="col-md-3 mt-3">
						<button type="button" className="btn btn-primary w-100">Add to Cart</button>
					</div>
				</div>
    		</div>
    	}
    	else
    	{
	    	avail_stock = 	
	    	<div>
				<label className="btn-sm btn-danger px-4 mt-2">Out of Stock</label>
			</div>		
    	}
    }

	return (
        <div>
            <div className="py-3 bg-warning">
                <div className="container">
                    <h6>Collections / {product.category.name} / {product.name}</h6>
                </div>
            </div>

            <div className="py-3">
                <div className="container">
                    <div className="row">
                    	<div className="col-md-4 border-end">
                    		<img src={`${axios.defaults.baseURL}${product.image}`} alt={product.name} className="w-100"/>
                    	</div>
                    	<div className="col-md-8">
                    		<h4>
                    			{product.name}
                    			<span className="float-end badge btn-sm btn-danger badge-pil"> {product.brand} </span>
                    		</h4>
                    		<p> {product.description} </p>
                    		<h4 className="mb-1">
                    			Rp. {product.selling_price}
                    			<s className="ms-2"> Rp. {product.original_price} </s>
                    		</h4>
                    		<div>
                    			{avail_stock}
                    		</div>
                    		<button type="button" className="btn btn-danger mt-3">Add to Wishlist</button>
                    	</div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProductDetail;
