import React, { useEffect, useState } from 'react';

function ViewCategory()
{

    const [loading, setLoading] = useState(true);
    const [category, setCategoryList] = useState([]);

    useEffect(()=>{
        axios.get('/api/getCategory').then(res => {
            if(res.status === 200)
            {
                setCategoryList(res.data.category);
                setLoading(false);
            }
        });
    });

    if(loading)
    {
        return <h4>Loading Categories...</h4>
    }
    else
    {
        var showCategoryList = '';
        
        showCategoryList =
        category.map((item)=>{
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.slug}</td>
                    <td>{item.status}</td>
                    <td>
                        <Link to={`edit-category/${item.id}`} className="btn btn-primary btn-sm">Edit</Link>
                    </td>
                    <td>
                        <button type="button" onClick={(e)=>deleteCategory(e, item.id)} className="btn btn-danger btn-sm">Delete</button>
                    </td>
                </tr>
            )
        });
    }

    return (
        <div>
            <div className="py-3 bg-warning">
                <div className="container">
                    <h6>Category Page</h6>
                </div>
            </div>

            <div className="py-3">
                <div className="container">
                    <div className="row">
                        <h6>Category Data</h6>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ViewCategory;
