import React, { useEffect, useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import {Link, useHistory} from 'react-router-dom';

function ProductDetail(props)
{

    const history = useHistory();
    const [loading, setLoading] = useState(true);
    const [product, setProduct] = useState([]);

    useEffect(()=>{
        let isMounted = true;

        const category_slug = props.match.params.category;
        const product_slug = props.match.params.product;
        axios.get(`/api/view-product/${category_slug}/${product_slug}`).then(res => {
            if(isMounted)
            {
                if(res.data.status === 200)
                {
                    setProduct(res.data.product);
                    setLoading(false);
                }
                else if(res.data.status === 404)
                {
                    history.push('/collections');
                    swal("Warning",res.data.message,"error");
                }
            }
        });
        
        return () => {
            isMounted = false
        };

    },[props.match.params.category, props.match.params.product, history]);

    if(loading)
    {
        return <h4>Loading Products Details...</h4>
    }
    else
    {
        var showProductList = '';
        
        if (productCount) 
        {
            showProductList = product.map((item, idx)=>{
                return (
                    <div className="col-md-3" key={idx}>
                        <div className="card">
                            <Link to={`/collections/${item.category.slug}/${item.slug}`}>
                                <img src={`${axios.defaults.baseURL}${item.image}`} className="w-100" alt={item.name} />
                            </Link>
                            <div className="card-body">
                                <Link to={`/collections/${item.category.slug}/${item.slug}`}>
                                    <h5>{item.name}</h5>
                                </Link>
                            </div>
                        </div>
                    </div>
                )
            });
        } 
        else 
        {
            showProductList = 
            <div className="col-md-12">
                <h4>No Product Available for {category.name}</h4>
            </div>
        }
    }

	return (
        <div>
            <div className="py-3 bg-warning">
                <div className="container">
                    <h6>Collections / category / product</h6>
                </div>
            </div>

            <div className="py-3">
                <div className="container">
                    <div className="row">
                    	<div className="col-md-4 border-end">
                    		<img src="" alt="Product Image" className="w-100"/>
                    	</div>
                    	<div className="col-md-8">
                    		<h4>
                    			Product Name
                    			<span className="float-end badge btn-sm btn-danger badge-pil"> Brand </span>
                    		</h4>
                    		<p> Product Description </p>
                    		<h4 className="mb-1">
                    			Rp. 1700000
                    			<s className="ms-2"> Rp. 2000000 </s>
                    		</h4>
							<div>
								<label className="btn-sm btn-success px-4 mt-2">In Stock</label>
                    			<div className="row">
                    				<div className="col-md-3 mt-3">
                    					<div className="input-group">
                    						<button type="button" className="input-group-text">-</button>
                    						<input text="text" className="form-control text-center" value="1"/>
                    						<button type="button" className="input-group-text">+</button>
										</div>
									</div>
									<div className="col-md-3 mt-3">
                						<button type="button" className="btn btn-primary w-100">Add to Cart</button>
									</div>
								</div>
                    		</div>
                    		<button type="button" className="btn btn-danger mt-3">Add to Wishlist</button>
                    	</div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProductDetail;
